package homework5;

import java.io.*;
import java.util.Base64;

public class MyClassLoader extends ClassLoader{
    // 获得classPath
    private final String classPath = this.getClass().getResource("").toString().substring(6);
    // 指定的编码字符串
    private final String fileString = "";
    // base64解码器
    private final Base64.Decoder decoder = Base64.getDecoder();

    @Override
    public Class findClass(String name) throws ClassNotFoundException {
        byte[] b = loadClassFromFile(name);
        return defineClass(name, b, 0, b.length);
    }

    /**
     * 将编码的字符串解码并写入.class文件
     * @param fileName 指定的文件名
     * @return 类的内容
     */
    private byte[] loadClassFromFile(String fileName) {
        // 对字符串进行base64解码
        byte[] fileByte = decoder.decode(fileString);
        // 在classPath下创建对应的.class文件进行写入
        File file = new File(classPath + fileName + ".class");
        try {
            OutputStream out = new FileOutputStream(file);
            out.write(fileByte);
            out.close();
        } catch (IOException e) {
            e.printStackTrace();
        }
        return fileByte;
    }
}
